import './Conversations.css'
import axios from 'axios'
import {useState,useEffect} from 'react'

const Conversations=({conversation,currentUser})=>{
    const [user,setUser]=useState(null)

    useEffect(()=>{
        const friendId=conversation.members.find((m)=>m!==currentUser._id)

        const getUser = async () =>{
            try{
            const res= await axios("/manjari?userId=" + friendId)
            setUser(res.data)
            //console.log(res.data.name)
            }
            catch(err)
            {
                console.log(err)
            }
        }
        getUser();
    },[currentUser,conversation])

    //console.log(user)
    return(

        <div className="conversation">
            <img className="conversationImg"
                src="https://images.unsplash.com/photo-1524504388940-b1c1722653e1?ixid=MnwxMjA3fDB8MHxzZWFyY2h8NXx8cGVvcGxlfGVufDB8fDB8fA%3D%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=600&q=60"
                alt=""
            />
            <span className="conversationName">{user?.name}</span>
        </div>
    )
}

export default Conversations
