import axios from 'axios'
import React,{useState,useEffect} from 'react'
import './ChatOnline.css'

function ChatOnline({onlineUsers, currentId, setCurrentChat}) {

    const [friends,setFriends]=useState([])
    const [onlineFriends,setOnlineFriends]=useState([])

    useEffect(()=>{
        const getFreinds=async()=>{
            try{  

            const res=await axios.get("/friends/" + currentId)
            console.log("1")

            setFriends(res.data)
            console.log(res)
            }
            catch(err){
                console.log(err)
            }
        }  
        getFreinds();      
    },[currentId]);

    console.log(friends)
    console.log(currentId)

    useEffect(()=>{
        setOnlineFriends(friends.filter((f)=>onlineUsers.includes(f._id)))
        console.log("2")
    },[friends,onlineUsers]);

    console.log(onlineUsers)

    return (
        <>
        {!currentId?<h1></h1>
        :
        <div className="chatOnline">
            {onlineFriends.map((o)=>(
            <div className="chatOnlineFriend">
                <div className="chatOnlineImgContainer">
                    <img 
                    className="chatOnlineImg"
                    src="https://images.unsplash.com/photo-1524504388940-b1c1722653e1?ixid=MnwxMjA3fDB8MHxzZWFyY2h8NXx8cGVvcGxlfGVufDB8fDB8fA%3D%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=600&q=60" 
                    alt="" />
                    <div className="chatOnlineBadge"></div>
                </div>
                <span className="chatOnlineName">{o?.name}</span>
            </div>
            ))}
        </div>
        }
        </>
    )
}

export default ChatOnline
