import React,{useState,useEffect} from 'react'
import './ChatOnline.css'
import axios from "axios"

function ChatOnline({conversation,currentUser}) {

    const [user,setUser]=useState(null)

    useEffect(()=>{
        const friendId=conversation.members.find((m)=>m!==currentUser._id)

        const getUser = async () =>{
            try{
            const res=await axios("/user/:id" + friendId)
            console.log(res)
            }
            catch(err)
            {
                console.log(err)
            }
        }
        getUser();
    },[currentUser,conversation])
    return (
        <div className="chatOnline">
            <div className="chatOnlineFriend">
                <div className="chatOnlineImgContainer">
                    <img 
                    className="chatOnlineImg"
                    src="https://images.unsplash.com/photo-1524504388940-b1c1722653e1?ixid=MnwxMjA3fDB8MHxzZWFyY2h8NXx8cGVvcGxlfGVufDB8fDB8fA%3D%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=600&q=60" 
                    alt="" />
                    <div className="chatOnlineBadge"></div>
                </div>
                <span className="chatOnlineName">John Doe</span>
            </div>
        </div>
    )
}

export default ChatOnline
